INSERT INTO `modx_site_snippets` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `moduleguid`, `static`, `static_file`) VALUES
(8, 0, 0, 'TinyMagicPublisherModifier ', 'Get/Preserve exact content (esp. during error postback) that include MODX tags HTML entities.[[*pagetitle:TinyMagicPublisherModifier]]\n\"pagetitle\",\"alias\",\"menutitle\",\"longtitle\",\"introtext\",\"description\",\"content\" ... anytextareas', 0, 7, 0, '//TinyMagicPublisherModifier Snippet\n//empty value gets default_text for both new and existing docs\nif (isset($_GET[\'edit\']) && $_GET[\'edit\'] == \'true\' || isset($_GET[\'create\']) && $_GET[\'create\'] == \'true\' ) {\n  if($input){\n    if ($modx->resource->get(\'createdby\') == $modx->user->get(\'id\') ) {\n      $legalOWNER = 1;\n    }\n    if (!$legalOWNER || !$modx->hasPermission(\'edit_document\') && !$modx->hasPermission(\'new_document\') ) {\n      return;\n    }\n    $typesOfResourcesFields = array(\"pagetitle\",\"alias\",\"menutitle\",\"longtitle\",\"introtext\",\"description\",\"content\");\n    if(in_array($name, $typesOfResourcesFields)) {\n      if($_POST[$name]){\n        $name = $_POST[$name];\n      }\n      else{\n        $name = $modx->resource->get($name);\n      }\n    }\n    else{\n      if($_POST[$name]){\n        if($_POST[$name] == \'\'){\n          $name = \'\';\n        }\n        $name = $_POST[$name];\n      }\n      else{\n        $name = $modx->resource->getTVValue($name);\n      }\n    }\n    $name = str_replace(array(\'[[\',\']]\'),array(\'{{\',\'}}\'),$name);\n    return $name;\n  }\n}', 0, 'a:0:{}', '', 0, ''),
(9, 0, 0, 'TinymceWrapperGRF', 'TinymceWrapper Plugin uses GetResourceField to grab the url of your elFinder resource. Since I don\'t know if you have friendly url on or not, I had no choice. Please replace this in your Plugin property, enter the url of your elFinder resource.', 0, 13, 0, '$resource = $modx->getObject(\'modResource\', array($kF =>$kFv));\nif($resource){\n	$output = $resource->get($gNuFv);\nreturn $output;\n	}', 0, 'a:3:{s:5:\"gNuFv\";a:7:{s:4:\"name\";s:5:\"gNuFv\";s:4:\"desc\";s:23:\"getNewUnknownFieldValue\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";N;s:4:\"area\";s:0:\"\";}s:2:\"kF\";a:7:{s:4:\"name\";s:2:\"kF\";s:4:\"desc\";s:10:\"knownField\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";N;s:4:\"area\";s:0:\"\";}s:3:\"kFv\";a:7:{s:4:\"name\";s:3:\"kFv\";s:4:\"desc\";s:15:\"knownFieldValue\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:0:\"\";s:7:\"lexicon\";N;s:4:\"area\";s:0:\"\";}}', '', 0, ''),
(10, 0, 0, 'TinymceWrapperGetUrlParam', 'This extends your elFinder, gives you url parameter emperor status', 0, 13, 0, '/**\n *\n * forked from getUrlParam by Paul Merchant\n *\n * A simple snippet to return a value passed through a URL parameter.\n *\n * OPTIONS\n * name - The parameter name, defaults to p\n * int - (Opt) Set as true to only allow integer values\n * max - (Opt) The maximum length of the returned value, defaults to 20\n * default - (Opt) The value returned if no URL parameter is found\n *\n * Example: [[TinymceWrapperGetUrlParam? &name=`pageid` &int=`1`]]\n *\n**/\n\n// set defaults\n$name = $modx->getOption(\'name\',$scriptProperties,\'p\');\n$int = $modx->getOption(\'int\',$scriptProperties,false);\n$max = $modx->getOption(\'max\',$scriptProperties,20);\n$output = $modx->getOption(\'default\',$scriptProperties,\'\');\n\n// get the sanitized value if there is one\nif (isset($_GET[$name])) {\n    if ($int) {\n        $value = intval($_GET[$name]);\n    } else {\n        if (strlen($_GET[$name]) > $max) {\n            $value = filter_var(substr($_GET[$name],0,$max), FILTER_SANITIZE_STRING);\n        } else {\n            $value = filter_var($_GET[$name], FILTER_SANITIZE_STRING);\n        }\n    }\n\n    $output = rawurldecode($value);\n		if($name == \'onlyMimes\'){\n		$output = \'[\"\'.$output.\'\"]\';\n	}\n		if($name == \'user\'){\n		$output = \'?name=\'.$output;\n	}\n}\n\nreturn $output;', 0, 'a:0:{}', '', 0, ''),
(11, 0, 0, 'TinymceWrapperMarkdown', 'Output modifier. It can also be used with other modifiers.\n[[*content:TinymceWrapperMarkdown]] ... [[+placeholder:TinymceWrapperMarkdown]]\nTinymceWrapperMarkdown = `markdownE` or `parsedown` default is parsedownE', 0, 13, 0, '/*\nby donshakespeare\nTinymceWrapperMarkdown is an Output Modifier for MODX\n[[*content:TinymceWrapperMarkdown=`markdown`]]\nor `markdownE` or `parsedown`\ndefault =`parsedownE`\nparsedownE or markdownE is good for Markdown mixed with HTML, and Markdown enclosed in HTML markdown=\"1\"*/\nif($input){\n    $content = $input;\n    // Parse MODX tags first || parse BBCodes here too\n    $content = $modx->newObject(\'modChunk\')->process(null, $content);\n    $options = explode(\',\',$options);\n    if($options[0] == \"markdown\"){\n        if (!class_exists(\'\\Michelf\\Markdown\')) {\n            require MODX_ASSETS_PATH . \'components/tinymcewrapper/markdown/Michelf/Markdown.inc.php\';\n        }\n        $content = \\Michelf\\Markdown::defaultTransform($content);\n    }\n    elseif($options[0] == \"markdownE\"){\n        if (!class_exists(\'\\Michelf\\MarkdownExtra\')) {\n            require MODX_ASSETS_PATH . \'components/tinymcewrapper/markdown/Michelf/MarkdownExtra.inc.php\';\n        }\n        $content = \\Michelf\\MarkdownExtra::defaultTransform($content);\n    }\n    elseif($options[0] == \"parsedown\"){\n        require MODX_ASSETS_PATH . \'components/tinymcewrapper/markdown/parsedown/Parsedown.php\';\n        $Parsedown = new Parsedown();\n        $content = $Parsedown->text($content);\n    }\n    else{ //default state\n        $options[0] = \"parsedownExtra\";\n        require MODX_ASSETS_PATH . \'components/tinymcewrapper/markdown/parsedown/Parsedown.php\';\n        require MODX_ASSETS_PATH . \'components/tinymcewrapper/markdown/parsedown/ParsedownExtra.php\';\n        $ParsedownExtra = new ParsedownExtra();\n        $content = $ParsedownExtra->text($content);\n    }\n    if(isset($options[1])){\n        $squareBracketSeparator = substr($options[1], 0, 1);\n        $content = str_replace(\'[\'.$squareBracketSeparator.\'[\', \'&#91;&#91;\', $content); // do some more find/replace\n    }\n    return $content);\n}', 0, 'a:0:{}', '', 0, ''),
(12, 0, 0, 'Wayfinder', 'Wayfinder for MODx Revolution 2.0.0-beta-5 and later.', 0, 0, 0, '/**\n * Wayfinder Snippet to build site navigation menus\n *\n * Totally refactored from original DropMenu nav builder to make it easier to\n * create custom navigation by using chunks as output templates. By using\n * templates, many of the paramaters are no longer needed for flexible output\n * including tables, unordered- or ordered-lists (ULs or OLs), definition lists\n * (DLs) or in any other format you desire.\n *\n * @version 2.1.1-beta5\n * @author Garry Nutting (collabpad.com)\n * @author Kyle Jaebker (muddydogpaws.com)\n * @author Ryan Thrash (modx.com)\n * @author Shaun McCormick (modx.com)\n * @author Jason Coward (modx.com)\n *\n * @example [[Wayfinder? &startId=`0`]]\n *\n * @var modX $modx\n * @var array $scriptProperties\n * \n * @package wayfinder\n */\n$wayfinder_base = $modx->getOption(\'wayfinder.core_path\',$scriptProperties,$modx->getOption(\'core_path\').\'components/wayfinder/\');\n\n/* include a custom config file if specified */\nif (isset($scriptProperties[\'config\'])) {\n    $scriptProperties[\'config\'] = str_replace(\'../\',\'\',$scriptProperties[\'config\']);\n    $scriptProperties[\'config\'] = $wayfinder_base.\'configs/\'.$scriptProperties[\'config\'].\'.config.php\';\n} else {\n    $scriptProperties[\'config\'] = $wayfinder_base.\'configs/default.config.php\';\n}\nif (file_exists($scriptProperties[\'config\'])) {\n    include $scriptProperties[\'config\'];\n}\n\n/* include wayfinder class */\ninclude_once $wayfinder_base.\'wayfinder.class.php\';\nif (!$modx->loadClass(\'Wayfinder\',$wayfinder_base,true,true)) {\n    return \'error: Wayfinder class not found\';\n}\n$wf = new Wayfinder($modx,$scriptProperties);\n\n/* get user class definitions\n * TODO: eventually move these into config parameters */\n$wf->_css = array(\n    \'first\' => isset($firstClass) ? $firstClass : \'\',\n    \'last\' => isset($lastClass) ? $lastClass : \'last\',\n    \'